mapper.update:
UPDATE company
SET company_password = #{newPassword}
WHERE company_name = #{companyName}

mapper.select:
SELECT *
FROM company
WHERE company_name = #{companyName} AND company_password = #{password}

SELECT *
FROM company
WHERE company_phone_num = #{phoneNum} AND company_password = #{password}

SELECT count(id)
FROM company
WHERE company_name = #{companyName}
